/**
 * Constants for seed user IDs and relationship data.
 * These match the deterministic IDs generated by `packages/db/src/seed.ts`.
 *
 * Run `bun run db:seed` to populate the database with this data.
 */

function seedId(name: string): string {
	return `seed_${name}`.padEnd(25, "0");
}

